import numpy as npimport sysimport caffeimport scipy.io as sio# this file fill the PCA values in the net structureeigenvectors_mat = sio.loadmat("eigenvectors.mat")eigenvectors = eigenvectors_mat['coeff']mean_dat = sio.loadmat("mean.mat")mean = mean_dat['mu']# layer_name = 'PCA'## # caffe.set_mode_cpu()net = caffe.Net("DeepModel.prototxt", "base_pca.caffemodel", caffe.TEST)##for id1 in xrange(42):    # eigenvectors initialization    for id2 in xrange(30):        net.params['PCA'][0].data[id1][id2] = eigenvectors[id1][id2]    # mean initialization    net.params['PCA'][1].data[id1] = mean[id1][0]net.save("base.caffemodel")# weights = net.params[layer_name][0].data# bias = net.params[layer_name][1].data# print (weights.shape[0])